export class EventEmitter {
    constructor() {
        this.listeners = [];
        this.event = (listener) => {
            this.listeners.push(listener);
            return { dispose: () => (this.listeners = this.listeners.filter(l => l !== listener)) };
        };
    }
    fire(data) {
        this.listeners.forEach(listener => listener(data));
    }
    dispose() {
        this.listeners = [];
    }
}
