import test from 'ava';
import { htmlTreeAsString } from '../dom';
test('html to string', function (t) {
    var elem1 = {
        tagName: 'div',
        id: '123',
        className: '99',
        name: 'elem1',
        getAttribute: function (id) {
            return this[id];
        },
    };
    var elem2 = {
        tagName: 'a',
        className: 'aaa',
        title: 'elem2',
        parentNode: elem1,
        getAttribute: function (id) {
            return this[id];
        },
    };
    t.is(htmlTreeAsString(elem2), 'div#123.99[name="elem1"] > a.aaa[title="elem2"]');
});
test('it work when element getAttribute return null', function (t) {
    var elem1 = {
        tagName: 'LINK',
        getAttribute: function () {
            return null;
        },
    };
    t.is(htmlTreeAsString(elem1), 'link');
});//# sourceMappingURL=https://trae.private/sourcemaps/424b4bd987c6c6a4cadf1a08da420593cc6bf75a/node_modules/@slardar/integrations/node_modules/@slardar/sdk-web/esm/utils/__test__/dom.spec.js.map